function a(t){const n=document.querySelector(".theme-switch i.fas");n.classList.toggle("fa-moon",t==="dark"),n.classList.toggle("fa-sun",t!=="dark")}let e="light";document.documentElement.setAttribute("data-bs-theme",e);window.matchMedia("(prefers-color-scheme: dark)").addEventListener("change",()=>{e="light",document.documentElement.setAttribute("data-bs-theme",e)});window.addEventListener("load",()=>{a("light")});
